mapper.update:
update community set   BOARD_RE_SEQ=BOARD_RE_SEQ + 1 
					where BOARD_RE_REF = #{BOARD_RE_REF} 
					and   BOARD_RE_SEQ  >  #{BOARD_RE_SEQ}

mapper.select:
select * from community 
where BOARD_NUM=#{num}
and	  BOARD_PASS=#{pass}

mapper.update:
update community
set BOARD_READCOUNT=BOARD_READCOUNT+1 
where BOARD_NUM = #{number}
